// src/Signup.js
import React, { useState } from 'react';
import { Auth } from 'aws-amplify';

function Signup() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [errors, setErrors] = useState({});
  const [stage, setStage] = useState('signup'); // 'signup' or 'verify'

  const handleSubmit = async (event) => {
    event.preventDefault();
    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
    } else {
      try {
        await Auth.signUp({
          username,
          password,
          attributes: {
            email,
          },
        });
        setStage('verify');
      } catch (error) {
        console.log('error signing up:', error);
        setErrors({ signup: error.message });
      }
    }
  };

  const validateForm = () => {
    const errors = {};
    if (!username) {
      errors.username = 'Username is required';
    }
    if (!email) {
      errors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      errors.email = 'Email is invalid';
    }
    if (!password) {
      errors.password = 'Password is required';
    } else if (
      !/^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/.test(password)
    ) {
      errors.password = 'Password must be at least 8 characters, include one uppercase letter, one number, and one symbol';
    }
    return errors;
  };

  const handleVerification = async (event) => {
    event.preventDefault();
    try {
      await Auth.confirmSignUp(username, verificationCode);
      alert('Successfully signed up and verified');
      // Redirect to signin page or another part of your app
    } catch (error) {
      console.log('error verifying sign up:', error);
      setErrors({ verify: error.message });
    }
  };

  return (
    <div>
      <h2>Signup Page</h2>
      {stage === 'signup' ? (
        <form onSubmit={handleSubmit}>
          <div>
            <label>Username: </label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            {errors.username && <span className="error">{errors.username}</span>}
          </div>
          <div>
            <label>Email: </label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            {errors.email && <span className="error">{errors.email}</span>}
          </div>
          <div>
            <label>Password: </label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            {errors.password && <span className="error">{errors.password}</span>}
          </div>
          <button type="submit">Signup</button>
          {errors.signup && <span className="error">{errors.signup}</span>}
        </form>
      ) : (
        <form onSubmit={handleVerification}>
          <div>
            <label>Verification Code: </label>
            <input
              type="text"
              value={verificationCode}
              onChange={(e) => setVerificationCode(e.target.value)}
            />
            {errors.verify && <span className="error">{errors.verify}</span>}
          </div>
          <button type="submit">Verify</button>
        </form>
      )}
    </div>
  );
}

export default Signup;
