import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './styles.css'; // Import the CSS file

const Signup = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Client-side validation
      if (!validatePassword(password)) {
        setErrorMessage('Password must contain at least one uppercase letter, one symbol, and one number');
        return;
      }

      // Make API call to check username uniqueness
      const isUsernameUnique = await checkUsernameUniqueness(username);
      if (!isUsernameUnique) {
        setErrorMessage('Username is already taken');
        return;
      }

      // Proceed with signup if validation passes
      const response = await fetch('https://njt7h9ukwj.execute-api.us-west-2.amazonaws.com/prod/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, email, password }), 
      });

      const data = await response.json();
      if (response.ok) {
        // Handle successful signup
      } else {
        // Handle signup error
      }
    } catch (error) {
      console.error('Error during signup:', error);
    }
  };

  // Password validation function
  const validatePassword = (password) => {
    const passwordRegex = /^(?=.*[A-Z])(?=.*\d)(?=.*[$@$!%*?&])[A-Za-z\d$@$!%*?&]{8,}$/;
    return passwordRegex.test(password);
  };

  // Simulate checking username uniqueness (replace with actual API call)
  const checkUsernameUniqueness = async (username) => {
    // Simulate API call delay
    await new Promise(resolve => setTimeout(resolve, 1000));
    // Mocked result (replace with actual logic)
    return username !== 'existingUsername';
  };

  return (
    <div className="container">
      <h2>Signup Page</h2>
      <div className="box">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="username">Username:</label>
            <input 
              type="text" 
              id="username" 
              value={username} 
              onChange={(e) => setUsername(e.target.value)} 
              required 
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input 
              type="email" 
              id="email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              required 
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input 
              type="password" 
              id="password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              required 
            />
          </div>
          {errorMessage && <p className="error-message">{errorMessage}</p>}
          <button type="submit">Signup</button>
        </form>
      </div>
      <p>Already have an account? <Link to="/signin">Sign In</Link></p>
    </div>
  );
};

export default Signup;
