import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './styles.css'; // Import the CSS file

const Signup = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [showVerificationCodeInput, setShowVerificationCodeInput] = useState(false);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Make API call to sign up the user
      const response = await fetch('https://njt7h9ukwj.execute-api.us-west-2.amazonaws.com/prod/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, email, password }), 
      });

      const data = await response.json();
      if (response.ok) {
        // Show verification code input field
        setShowVerificationCodeInput(true);
      } else {
        setErrorMessage(data.error || 'Signup failed');
      }
    } catch (error) {
      console.error('Error during signup:', error);
      setErrorMessage('Error during signup');
    }
  };

  const handleVerificationSubmit = async (event) => {
    event.preventDefault();
    try {
      // Make API call to verify the user
      const response = await fetch('https://your-verification-lambda-function-url', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, verificationCode }), 
      });

      const data = await response.json();
      if (response.ok) {
        // Handle successful verification
      } else {
        setErrorMessage(data.error || 'Verification failed');
      }
    } catch (error) {
      console.error('Error during verification:', error);
      setErrorMessage('Error during verification');
    }
  };

  return (
    <div className="container">
      <h2>Signup Page</h2>
      <div className="box">
        <form onSubmit={showVerificationCodeInput ? handleVerificationSubmit : handleSubmit}>
          <div className="form-group">
            <label htmlFor="username">Username:</label>
            <input 
              type="text" 
              id="username" 
              value={username} 
              onChange={(e) => setUsername(e.target.value)} 
              required 
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input 
              type="email" 
              id="email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              required 
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input 
              type="password" 
              id="password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              required 
            />
          </div>
          {showVerificationCodeInput && (
            <div className="form-group">
              <label htmlFor="verificationCode">Verification Code:</label>
              <input 
                type="text" 
                id="verificationCode" 
                value={verificationCode} 
                onChange={(e) => setVerificationCode(e.target.value)} 
                required 
              />
            </div>
          )}
          {errorMessage && <p className="error-message">{errorMessage}</p>}
          <button type="submit">{showVerificationCodeInput ? 'Verify' : 'Signup'}</button>
        </form>
      </div>
      <p>Already have an account? <Link to="/signin">Sign In</Link></p>
    </div>
  );
};

export default Signup;
