import React, { useState } from 'react';
import { CognitoUserPool, CognitoUserAttribute, CognitoUser } from 'amazon-cognito-identity-js';

const poolData = {
  UserPoolId: 'your-user-pool-id', // e.g., 'us-east-1_XXXXXXXXX'
  ClientId: 'your-client-id', // e.g., 'XXXXXXXXXX'
};
const userPool = new CognitoUserPool(poolData);

const Signup = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [isVerifying, setIsVerifying] = useState(false);

  const validatePassword = (password) => {
    const regex = /^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    return regex.test(password);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validatePassword(password)) {
      alert('Password must contain at least one uppercase letter, one number, and one special character.');
      return;
    }

    const attributeList = [
      new CognitoUserAttribute({ Name: 'email', Value: email }),
    ];

    userPool.signUp(username, password, attributeList, null, (err, result) => {
      if (err) {
        alert(err.message || JSON.stringify(err));
        return;
      }
      console.log('User signed up successfully:', result.user);
      setIsVerifying(true);
    });
  };

  const handleVerification = (e) => {
    e.preventDefault();
    const userData = {
      Username: username,
      Pool: userPool,
    };

    const cognitoUser = new CognitoUser(userData);
    cognitoUser.confirmRegistration(verificationCode, true, (err, result) => {
      if (err) {
        alert(err.message || JSON.stringify(err));
        return;
      }
      alert('User verified successfully!');
      console.log('Verification successful:', result);
    });
  };

  return (
    <div>
      {!isVerifying ? (
        <form onSubmit={handleSubmit}>
          <div>
            <label>Username:</label>
            <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} required />
          </div>
          <div>
            <label>Email:</label>
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </div>
          <div>
            <label>Password:</label>
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          </div>
          <button type="submit">Sign Up</button>
        </form>
      ) : (
        <form onSubmit={handleVerification}>
          <div>
            <label>Verification Code:</label>
            <input type="text" value={verificationCode} onChange={(e) => setVerificationCode(e.target.value)} required />
          </div>
          <button type="submit">Verify</button>
        </form>
      )}
    </div>
  );
};

export default Signup;
