import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import './styles.css'; // Import the CSS file

const Signin = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const history = useHistory();

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('https://njt7h9ukwj.execute-api.us-west-2.amazonaws.com/prod/signin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();
      if (response.ok) {
        // Navigate to the Fire page upon successful signin
        history.push('/fire');
      } else {
        setErrorMessage(data.error || 'Signin failed');
      }
    } catch (error) {
      console.error('Error during signin:', error);
      setErrorMessage('Error during signin');
    }
  };

  return (
    <div className="container">
      <h2>Signin Page</h2>
      <div className="box">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="username">Username:</label>
            <input 
              type="text" 
              id="username" 
              value={username} 
              onChange={(e) => setUsername(e.target.value)} 
              required 
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input 
              type="password" 
              id="password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              required 
            />
          </div>
          {errorMessage && <p className="error-message">{errorMessage}</p>}
          <button type="submit">Signin</button>
        </form>
      </div>
      <p>Don't have an account? <Link to="/signup">Sign Up</Link></p>
    </div>
  );
};

export default Signin;
