import React, { useState } from 'react';
import { useLocation, useHistory } from 'react-router-dom';

const Verification = () => {
  const [username, setUsername] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const history = useHistory();
  const location = useLocation();

  // Extract username from query parameters
  const queryParams = new URLSearchParams(location.search);
  const usernameFromParams = queryParams.get('username');

  // Set the username if available
  if (usernameFromParams) {
    setUsername(usernameFromParams);
  }

  const handleVerificationSubmit = async (event) => {
    event.preventDefault();
    try {
      // Make API call to verify the user
      const response = await fetch('https://njt7h9ukwj.execute-api.us-west-2.amazonaws.com/prod/verify', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, verificationCode }),
      });

      const data = await response.json();
      if (response.ok) {
        // Handle successful verification
        history.push('/signin'); // Redirect to signin page
      } else {
        setErrorMessage(data.error || 'Verification failed');
      }
    } catch (error) {
      console.error('Error during verification:', error);
      setErrorMessage('Error during verification');
    }
  };

  return (
    <div className="container">
      <h2>Verification Page</h2>
      <div className="box">
        <form onSubmit={handleVerificationSubmit}>
          <div className="form-group">
            <label htmlFor="username">Username:</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="verificationCode">Verification Code:</label>
            <input
              type="text"
              id="verificationCode"
              value={verificationCode}
              onChange={(e) => setVerificationCode(e.target.value)}
              required
            />
          </div>
          {errorMessage && <p className="error-message">{errorMessage}</p>}
          <button type="submit">Verify</button>
        </form>
      </div>
    </div>
  );
};

export default Verification;
