import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './signin.css'; // Import the CSS file for signin

const Signin = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate(); // Use useNavigate instead of useHistory

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('https://njt7h9ukwj.execute-api.us-west-2.amazonaws.com/prod/signin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();
      if (response.ok) {
        console.log('Login successful!', data);
        // Redirect to fire page only on successful login
        navigate('/fireworks'); // Use navigate function for navigation
      } else {
        // Handle signin error
        setError(data.error || 'Invalid username or password. Please try again.');
      }
    } catch (error) {
      console.error('Error during signin:', error);
      setError('An unexpected error occurred. Please try again later.');
    }
  };

  return (
    <div className="container">
      <div className="signin-box">
        <h2>Signin Form</h2>
        <div className="toggle-buttons">
          <Link to="/signin" className="toggle-button active">Login</Link>
          <Link to="/signup" className="toggle-button">Signup</Link>
        </div>
        {error && <p className="error-message">{error}</p>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="signin-button">Signin</button>
        </form>
        <p>Don't have an account? <Link to="/signup" className="signup-link">Sign Up</Link></p>
      </div>
    </div>
  );
};

export default Signin;
